(1, 2, 'CONF_SHOP_NAME', 'Фотобанк', 'Название магазина', 'Введенное значение будет добавляться в TITLE всех страниц магазина, а также в сообщения покупателям (например, в уведомления о заказах)', 'setting_TEXT_BOX(0,', 1),
(2, 2, 'CONF_SHOP_URL', 'key-foto.com', 'URL магазина', 'Адрес вашего магазина в Интернет, указываемый в письмах покупателю. Например, www.myshop.ru', 'setting_TEXT_BOX(0,', 3),
(3, 2, 'CONF_CHECKSTOCK', '0', 'Вести учет товаров на складе', 'В случае, если опция выключена, информация о наличии товаров на складе не будет обновляться и проверяться.', 'setting_CHECK_BOX(', 26),
(4, 1, 'CONF_UPDATE_GCV', '1', 'Автоматически обновлять количество товаров в категориях (в администрации)', 'Рекомендуется выключить эту опцию при количестве товаров более 10000 (десяти тысяч)', 'setting_CHECK_BOX(', 15),
(5, 2, 'CONF_DEFAULT_CURRENCY', '3', 'Валюта по умолчанию', 'Выберите валюту, которая будет использоваться по умолчанию', 'settingCONF_DEFAULT_CURRENCY()', 27),
(6, 2, 'CONF_GENERAL_EMAIL', 'dd@ixum.ru', 'Основной email адрес магазина', 'Используется как обратный адрес при контакте с покупателями', 'setting_TEXT_BOX(0,', 6),
(7, 2, 'CONF_ORDERS_EMAIL', 'dmitry5151@yandex.ru', 'Email для отправки уведомлений о заказах', 'По этому адресу будут отправляться уведомления о новых заказах', 'setting_TEXT_BOX(0,', 7),
(8, 2, 'CONF_NEW_ORDER_STATUS', '2', 'Статус нового заказа', 'Все новые заказы будут автоматически приобретать этот статус', 'settingCONF_NEW_ORDER_STATUS()', 28),
(9, 2, 'CONF_COMPLETED_ORDER_STATUS', '5', 'Статус завершенного (выполненного) заказа', 'Выберете статус заказа', 'settingCONF_COMPLETED_ORDER_STATUS()', 29),
(10, 2, 'CONF_PRODUCTS_PER_PAGE', '20', 'Максимальное количество товаров на странице', 'Введите максимальное количество товаров на странице при просмотре категорий и результатов поиска', 'setting_TEXT_BOX(2,', 11),
(11, 2, 'CONF_COLUMNS_PER_PAGE', '3', 'Количество столбцов при показе товаров на странице', 'при просмотре категорий и результатов поиска', 'setting_TEXT_BOX(2,', 10),
(12, 2, 'CONF_ONLINE_EXPIRE', '10', 'Количество минут пока посетитель считается on-line', 'Укажите время неактивности в минутах в течении которого посетитель магазина будет считаться on-line.', 'setting_TEXT_BOX(2,', 9),
(13, 2, 'CONF_SHOW_ADD2CART', '1', 'Возможность добавления товаров в корзину и оформления заказов', 'Выключите эту опцию, если вы хотите отключить возможность оформления заказов и добавления товаров в корзину', 'setting_CHECK_BOX(', 24),
(14, 2, 'CONF_PRODUCT_SORT', '1', 'Сортировка товаров', 'Предоставлять пользователям возможность сортировки товарных позиций в одной товарной категории по цене, наименованию, рейтингу', 'setting_CHECK_BOX(', 26),
(15, 1, 'CONF_COUNTRY', '0', 'Страна', 'Страна, в которой находится Ваш магазин (необходимо для расчета стоимости доставки)', 'settingCONF_COUNTRY()', 29),
(16, 1, 'CONF_ZONE', '0', 'Область', 'Область, в которой находится Ваш магазин (необходимо для расчета стоимости доставки)', 'settingCONF_ZONE()', 10),
(17, 1, 'CONF_DISCOUNT_TYPE', '1', 'Discount type', 'Discount type', 'settingCONF_DISCOUNT_TYPE()', 0),
(18, 1, 'CONF_ORDERING_REQUEST_BILLING_ADDRESS', '0', 'Запрашивать у покупателя адрес выставления счета (адрес плательщика)', 'Включите эту опцию, если адрес плательщика может отличаться от адреса доставки заказа, и Вы хотите запрашивать у покупателя адрес выставления счет отдельно от адрес доставки заказа', 'setting_CHECK_BOX(', 32),
(19, 2, 'CONF_DEFAULT_COUNTRY', '0', 'Страна по умолчанию', 'Cтрана по умолчанию в которой находится ваш магазин', 'settingCONF_DEFAULT_COUNTRY()', 40),
(20, 1, 'CONF_CALCULATE_TAX_ON_SHIPPING', '0', 'Доставка облагается налогом', 'Если на стоимость доставки необходимо вычислять налог, пожалуйста, укажите класс налогов, по которому будет вычисляться ставка налога. Если налог вычислять не нужно, выберете "нет"', 'settingCONF_CALCULATE_TAX_ON_SHIPPING()', 34),
(21, 2, 'CONF_DEFAULT_CUSTOMER_GROUP', '1', 'Группа по умолчанию', 'Группа покупателей, к которой будет автоматически отнесен каждый новый покупатель после регистрации, если включена система пользователей.', 'settingCONF_DEFAULT_CUSTOMER_GROUP()', 40),
(22, 1, 'CONF_DEFAULT_TAX_CLASS', '0', 'Класс расчета доставки', 'Класс расчета стоимости доставки для населенных пунктов, используемый в магазине.', 'setting_TEXT_BOX(0,', 33),
(23, 2, 'CONF_NEWS_COUNT_IN_CUSTOMER_PART', '3', 'Количество записей в блоках новостей', 'Пожалуйста, укажите, сколько ссылок показывать в блоках новостей магазина.', 'setting_TEXT_BOX(2,', 11),
(24, 2, 'CONF_NEWS_COUNT_IN_NEWS_PAGE', '10', 'Количество записей на странице новостей', 'Пожалуйста, укажите, сколько последних новостей показывать на странице с аннотациями', 'setting_TEXT_BOX(2,', 12),
(25, 2, 'CONF_PROTECTED_CONNECTION', '0', 'Заказ в защищенном режиме', 'Включите эту галочку, если Вы хотите, чтобы оформление заказов производилось в защищенном режиме SSL.<br>Для корректной работы этой функции на сервере должен быть установлен SSL сертификат.', 'setting_CHECK_BOX(', 25),
(26, 1, 'CONF_ADDRESSFORM_STATE', '2', 'Address form - state/province field', '', 'setting_TEXT_BOX(0,', 0),
(27, 1, 'CONF_ADDRESSFORM_CITY', '2', 'Address form - city', '', 'setting_TEXT_BOX(0,', 0),
(28, 1, 'CONF_ADDRESSFORM_ADDRESS', '2', 'Address form - street address', '', 'setting_TEXT_BOX(0,', 0),
(29, 2, 'CONF_DATE_FORMAT', 'DD.MM.YYYY', 'Формат даты', 'Пожалуйста, выберите формат отображения даты', 'setting_DATEFORMAT(', 26),
(30, 2, 'CONF_HOMEPAGE_META_KEYWORDS', '', 'META Keywords по умолчанию', 'Текст, который Вы введете здесь, будет добавлен в качестве META Keywords всех страниц магазина, у которых не определен свой META Keywords', 'setting_TEXT_BOX(0,', 5),
(31, 2, 'CONF_HOMEPAGE_META_DESCRIPTION', 'Фотобанк', 'META Description по умолчанию', 'Текст, который Вы введете здесь, будет добавлен в качестве META Description всех страниц магазина, у которых не определен свой META Description', 'setting_TEXT_BOX(0,', 4),
(32, 2, 'CONF_DEFAULT_TITLE', 'Фотобанк', 'Заголовок (TITLE) магазина', 'Текст, который Вы введете здесь, будет добавлен в качестве заголовка (TITLE) на все страницы магазина.', 'setting_TEXT_BOX(0,', 2),
(33, 1, 'CONF_OPEN_SHOPPING_CART_IN_NEW_WINDOW', '0', 'Открывать страницу корзины в новом окне', 'Включите эту опцию для того, чтобы страница корзины при добавлении нового товара в нее открывалась в отдельном окне (popup)', 'setting_CHECK_BOX(', 17),
(34, 1, 'CONF_WEIGHT_UNIT', 'kg', 'Единица измерения веса', 'Пожалуйста, выберите единицу измерения веса, в которой Вы указываете вес товаров в магазине', 'setting_WEIGHT_UNIT(', 26),
(35, 2, 'CONF_TAB_COUNT_IN_HOME', '3', 'Количество столбцов товаров на главной', 'Пожалуйста, укажите количество столбцов товаров на главной странице', 'setting_TEXT_BOX(0,', 9),
(36, 2, 'CONF_SMARTY_FORCE_COMPILE', '1', 'Принудительная очистка кэша', 'Настоятельно рекомендуется выключить, включать необходимо только при изменении файлов Вашего магазина. После того, как изменения будут законечены, выключите эту опцию (это увеличит скорость работы магазина)', 'setting_CHECK_BOX(', 20),
(37, 2, 'CONF_FULLY_EXPAND_CATEGORIES_IN_ADMIN_MODE', '1', 'Полностью "разворачивать" список категорий в администрировании', 'Рекомендуется выключить эту опцию, если количество категорий в Вашем магазине превышает 100. Это увеличит скорость загрузки страницы администрирования каталога.', 'setting_CHECK_BOX(', 25),
(38, 2, 'CONF_EDITOR', '1', 'Использовать визуальный редактор (в администрировании)', 'Включите данную опцию, если вы хотите использовать визуальный редактор контента.', 'setting_CHECK_BOX(', 22),
(39, 2, 'CONF_BACKEND_SAFEMODE', '0', 'Безопасный режим', 'Включать только если вы хотите сделать свой магазин демонстрационным и уже внесли все данные и настройки. <span style="color: red">Отключить опцию невозможно!</span>', 'setting_CHECK_BOX(', 26),
(40, 2, 'CONF_PRODUCT_MAIL', '1', 'Отображать форму "Вопросы по товарам"', 'Выберите, будет ли отображаться форма обратной связи в блоке подробного описания товара.', 'setting_CHECK_BOX(', 26),
(41, 2, 'CONF_MOD_REWRITE', '1', 'Псевдостатические ссылки', 'Преобразование динамических ссылок в псевдостатические. <span style="color: red">В случае отключения этой опции необходимо переименовать файл robots.txt в корне домена на robots_off.txt, при включении переименовать в robots.txt!</span>', 'setting_CHECK_BOX(', 22),
(42, 2, 'CONF_ACTIVE_ORDER', '0', 'Активация заказа', 'Включите эту опцию, если Вы хотите, чтобы покупателям высылалось подтверждение на e-mail для активации заказа.', 'setting_CHECK_BOX(', 26),
(43, 2, 'CONF_USER_SYSTEM', '1', 'Регистрация пользователей', 'Позволяет регистрироватся покупателям в магазине, пользоваться личным аккаутом, просматривать историю заказов и т.д..', 'settingCONF_USER_SYSTEM()', 27),
(44, 2, 'CONF_EMAIL_ORDER_SEND', '0', 'E-mail отчеты для покупателей', 'При оформлении заказа, у покупателей запрашивается e-mail и отсылается информация о заказе.', 'setting_CHECK_BOX(', 26),
(45, 3, 'CONF_PHOTO_RESIZE', '1', 'Изменять размер фотографий', 'Включите эту опцию если Вы хотите чтобы загружаемые фотографии автоматически преобразовывались в заданный размер. Для корректной работы этой функции на сервере должен быть установлен модуль GD.', 'setting_CHECK_BOX(', 1),
(46, 3, 'CONF_PHOTO_WIDTH1', '160', 'Максимальный размер фотографий для каталога (если включено изменение размера фотографий)', 'Введите максимальный размер в пикселях для фотографий каталога (xxx - максимальный размер по ширине или высоте, 0 - размер остается прежним).', 'setting_TEXT_BOX(0,', 2),
(47, 3, 'CONF_PHOTO_WIDTH2', '350', 'Максимальный размер фотографий для подробного описания (если включено изменение размера фотографий)', 'Введите максимальный размер в пикселях для фотографий подробного описания (xxx - максимальный размер по ширине или высоте, 0 - размер остается прежним).', 'setting_TEXT_BOX(0,', 3),
(48, 3, 'CONF_PHOTO_WIDTH3', '1200', 'Максимальный размер фотографий в полную величину (если включено изменение размера фотографий)', 'Введите максимальный размер в пикселях для фотографий в полную величину (xxx - максимальный размер по ширине или высоте, 0 - размер остается прежним).', 'setting_TEXT_BOX(0,', 4),
(49, 2, 'CONF_MAIL_HOST', 'host', 'Хост SMTP сервера (если выбрана SMTP отправка писем)', 'Введите адрес вашего SMTP сервера (почтовый mail-сервер) через который будет осуществляться отправка писем.', 'setting_TEXT_BOX(0,', 17),
(50, 2, 'CONF_MAIL_LOGIN', 'login', 'Логин для доступа к SMTP серверу (если выбрана SMTP отправка писем)', 'Введите логин для вашего SMTP сервера (почтовый mail-сервер) через который будет осуществляться отправка писем.', 'setting_TEXT_BOX(0,', 18),
(51, 2, 'CONF_MAIL_PASS', 'pass', 'Пароль для доступа к SMTP серверу (если выбрана SMTP отправка писем)', 'Введите пароль для вашего SMTP сервера (почтовый mail-сервер) через который будет осуществляться отправка писем.', 'setting_TEXT_BOX(0,', 19),
(52, 2, 'CONF_SECURITY_EXPIRE', '2', 'Количество часов, в течении которых магазин будет "помнить" посетителей', 'Укажите количество часов, в течении которых магазин будет "помнить" посетителей после последнего посещения. По истечении срока сессия удаляется.', 'setting_TEXT_BOX(0,', 8),
(53, 2, 'CONF_MINIMAL_ORDER_AMOUNT', '0', 'Минимальная сумма заказа', 'Возможность оформления заказов, сумма которых меньше указанной, будет заблокирована.<br />Укажите 0, чтобы отключить ограничение на минимальный заказ.', 'setting_TEXT_BOX(0,', 13),
(54, 2, 'CONF_SHOP_BILD', '0', 'Закрыть на профилактику', 'Выберите этот пункт, если вы хотите провести профилактические работы в магазине. Магазин будет доступен только для администраторов.', 'setting_CHECK_BOX(', 21),
(55, 2, 'CONF_AUTOSAVE', '0', 'Автосохранение', 'Выберите этот пункт, если хотите, чтобы вся база данных сохранялась раз в сутки. Дампы старше недели автоматически удаляются.', 'setting_CHECK_BOX(', 26),
(56, 2, 'CONF_DEFAULT_TEMPLATE', 'Key-foto', 'Шаблон пользовательской части', 'Выберите шаблон(дизайн) для пользовательской части магазина.', 'settingCONF_DEFAULT_TEMPLATE()', 27),
(57, 2, 'CONF_CART_METHOD', '2', 'Метод работы корзины товаров', '"Стандартный" - после выбора товара, покупатель отправляется в корзину. "В новом окне" - корзина открывается в новом окне, покупатель продолжает выбор товаров. "AjaxCart" - корзина не открывается, покупатель продолжает выбор товаров.', 'settingCONF_SELECT_CART_METHOD()', 27),
(58, 2, 'CONF_ALLOW_COMPARISON_FOR_SIMPLE_SEARCH', '0', 'Разрешить сравнение товаров в результатах простого поиска', 'Включите, чтобы разрешить покупателям сравнивать товары при простом поиске (по имени и описанию)', 'setting_CHECK_BOX(', 26),
(59, 2, 'CONF_ENABLE_CONFIRMATION_CODE', '1', 'Проверка ввода случайного кода, изображенного на графическом рисунке', 'Данная функция повышает защищенность форм регистрации и быстрого оформления заказа от автоматизированного ввода данных (например, роботами)', 'setting_CHECK_BOX(', 26),
(60, 2, 'CONF_EXACT_PRODUCT_BALANCE', '0', 'Показывать пользователям точный остаток товаров на складе', 'Если опция включена, покупателям будет показано кол-во товара на складе (например, "На складе: 34"). Если опция выключена, то отображается только факт наличия или отсутствия товара (например, "На складе: да")', 'setting_CHECK_BOX(', 26),
(61, 2, 'CONF_USE_GZIP', '1', 'Потоковое сжатие (gzip)', 'Если данная опция включена, вся информация выдаваемая сервером будет проходить потоковое сжатие. Опция значительно ускоряет открытие страниц магазина и существенно экономит траффик.', 'setting_CHECK_BOX(', 26),
(62, 2, 'CONF_DEFAULT_SORT_ORDER', 'sort_order, name', 'Сортировка по умолчанию', 'Выберите, в каком порядке по умолчанию будут сортироваться товары в категориях и результатах поиска.', 'settingCONF_DEFAULT_SORT_ORDER()', 39),
(63, 2, 'CONF_DISPLAY_INFO', '0', 'Показывать отладочную информацию', 'Опция позволяет отображать отладочную информацию для программистов и дизайнеров в режиме администратора в пользовательской части магазина на специальной панели в верхней части страницы.', 'setting_CHECK_BOX(', 23),
(64, 2, 'CONF_NOTIFY_STOCKADMIN', '0', 'Отправлять уведомления администратору если товар на складе закончился', 'Если опция включена, администратор будет автоматически получать уведомления на e-mail о том, что товар на складе закончился.', 'setting_CHECK_BOX(', 26),
(65, 2, 'CONF_AUTOOFF_STOCKADMIN', '0', 'Отключать товар если он закончился на складе', 'Опция позволяет автоматически отключать товар когда он заканчивается на складе.', 'setting_CHECK_BOX(', 26),
(66, 2, 'CONF_SHOW_COUNTPROD', '1', 'Показывать количество товаров в категориях', 'Включите эту опцию, если вы хотите чтобы в блоках меню и каталоге показывалось число товаров в категориях.', 'setting_CHECK_BOX(', 26),
(67, 2, 'CONF_DISPLAY_PRCODE', '1', 'Отображать код товара на страницах каталога', 'Включите, если вы хотите чтобы на страницах каталога и прайс-листа отображался код(артикул) товара.', 'setting_CHECK_BOX(', 26),
(68, 2, 'CONF_DISP_ORDERNUM', '1', 'Показывать номер заказа при успешном оформлении', 'Опция позволяет покупателям видеть номер их заказа на странице сообщения об успешном оформлении заказа.', 'setting_CHECK_BOX(', 26),
(69, 3, 'CONF_DISPLAY_NOPHOTO', '1', 'Выводить картинку-заглушку ("нет фотографии") при отсутствии фотографий у товара', 'Если данная опция включена, при отсутствии у товара фотографий, на страницах каталога будет выводиться стандартная картинка-заглушка "нет фотографии".', 'setting_CHECK_BOX(', 5),
(70, 2, 'CONF_DISP_INDEXCART', '0', 'Возможность заказа товаров с главной страницы', 'Опция позволяет покупателям делать заказ товаров, которые выводятся на главной странице магазина. Если товар имеет характеристики они не будут учтены при заказе.', 'setting_CHECK_BOX(', 26),
(71, 2, 'CONF_USE_DISCUSS', '1', 'Разрешить покупателям оставлять мнения о товаре', 'Если данная опция включена, покупателям предоставляется возможность оставлять свои комментарии и мнения о товарах .', 'setting_CHECK_BOX(', 26),
(72, 2, 'CONF_ORDER_ESCAPE', '0', 'Разрешить отмену заказов', 'Опция позволяет покупателям самостоятельно отменять свои заказы, если по каким-либо причинам они от него отказываются. Ссылка для отмены заказа приходит вместе с данными на e-mail.', 'setting_CHECK_BOX(', 26),
(73, 2, 'CONF_SHOW_NULL_STOCK', '0', 'Не показывать товары которых нет на складе', 'Опция запрещает показывать в каталоге те товары, которых нет на складе (если включен учет товаров на складе).', 'setting_CHECK_BOX(', 26),
(74, 2, 'CONF_USE_RATING', '1', 'Включить систему рейтинга', 'Если данная опция включена, покупателям предоставляется возможность голосовать за товары и сортировать их по уровню популярности.', 'setting_CHECK_BOX(', 26),
(75, 2, 'CONF_SHOW_PARENCAT', '0', 'Выводить список подкатегорий с описаниями', 'Опция полезна, если вы хотите выводить не только товары на странице категории, но и список подкатегорий с их описаниями и фотографиями.', 'setting_CHECK_BOX(', 26),
(76, 2, 'CONF_SECURE_SESSIONS', '1', 'Использовать безопасные сессии', 'При использовании данной опции ip адрес и поле user_agent будут сверяться с начальным значением при старте сессии.', 'setting_CHECK_BOX(', 26),
(77, 2, 'CONF_MAIL_METHOD', '1', 'Способ отправки почты', 'Выберите способ отправки e-mail сообщений. Это может быть серверная функция mail(), либо функция отправки через SMTP протокол(использовать только если mail() не работает).', 'settingCONF_MAIL_METHOD()', 27),
(78, 2, 'CONF_TIMEZONE', '0', 'Часовой пояс (GMT)', 'Если время на вашем сервере не соответствует установленному в вашем часовом поясе, вы можете выбрать один из 24 часовых поясов. Внимание! Выставлять часовой пояс необходимо относительно времени вашего сервера!', 'settingCONF_TIMEZONE()', 27),
(79, 3, 'CONF_DISPLAY_FOTO', '1', 'Угол отображения фотографий товаров', 'Выберите угол отображения фотографий товаров на страницах магазина (справа или слева от описания).', 'settingCONF_DISPLAY_FOTO()', 8),
(80, 3, 'CONF_WATERMARK_IMAGE', 'wm.png', 'Копирайт-картинка(водяной знак)', 'Укажите изображение находящееся в папке data/, которое будет использоваться в качестве водяного знака. Например: watermark.png', 'setting_TEXT_BOX(0,', 7),
(81, 3, 'CONF_PUT_WATERMARK', '1', 'Накладывать на загружаемые фотографии копирайт-картинку(водяной знак)', 'Включите эту опцию если Вы хотите чтобы на загружаемые фотографии автоматически накладывалась копирайт-картинка(водяной знак) указанная в настройках.', 'setting_CHECK_BOX(', 6),
(82, 2, 'CONF_MULTYCART', '0', 'Разрешить указывать количество товара для добавления в корзину', 'Если опция включена покупатель может указывать необходимое ему количество товара непосредственно перед добавлением товара в корзину.', 'setting_CHECK_BOX(', 26),
(83, 4, 'CONF_AFFILIATE_EMAIL_NEW_PAYMENT', '0', 'Отправлять e-mail покупателю о новом платеже', '', 'setting_CHECK_BOX(', 0),
(84, 4, 'CONF_AFFILIATE_PROGRAM_ENABLED', '0', 'Партнерская программа', 'Включить/выключить', 'setting_CHECK_BOX(', 0),
(85, 4, 'CONF_AFFILIATE_AMOUNT_PERCENT', '10', 'Процент комиссии', '', 'setting_TEXT_BOX(0,', 0),
(86, 4, 'CONF_AFFILIATE_EMAIL_NEW_COMMISSION', '0', 'Отправлять e-mail покупателю о новой комиссии', '', 'setting_CHECK_BOX(', 0),
(87, 2, 'CONF_ENABLE_REGCONFIRMATION', '0', 'Требовать активацию учетной записи покупателя после регистрации', 'Если данная опция включена, покупателю будет отправляться по электронной почте ключ активации, который он должен ввести после заполнения регистрационной формы. Это позволяет проверять правильность ввода электронного адреса', 'setting_CHECK_BOX(', 26),
(88, 2, 'CONF_CHAINLINKS_AUTO', '0', 'Перелинковка новых товаров', 'Автоматически генерировать ссылки для товаров, добавляемых в магазин', 'setting_TEXT_BOX(0,', 27),
(89, 2, 'CONF_CHAINLINKS_CATLIST', '', 'Список категорий перелинковки', 'Список ID категорий через запятую. Если параметр задан - перелинковка ограничится только этими категориями', 'setting_TEXT_BOX(0,', 27);
